    private void Memo11_BeforePrint(object sender, EventArgs e)
    {
      String flat,room,korp;
      flat = &quot;&quot;;
      korp = &quot;&quot;;
      room = &quot;&quot;;
      if ((string)Report.GetColumnValue(&quot;Main.nflat&quot;) != &quot;&quot; &amp;&amp; (string)Report.GetColumnValue(&quot;Main.nflat&quot;) != &quot;0&quot;)
        flat = &quot; кв.&quot;+(string)Report.GetColumnValue(&quot;Main.nflat&quot;);
      else 
        flat = &quot;&quot;;
      if ((string)Report.GetColumnValue(&quot;Main.home_korp&quot;) != &quot;&quot;)
        korp = &quot; корп.&quot;+(string)Report.GetColumnValue(&quot;Main.home_korp&quot;);
      else 
        korp = &quot;&quot;;
      if ((string)Report.GetColumnValue(&quot;Main.numberroom&quot;) != &quot;&quot;)
        room = &quot; ком.&quot;+(string)Report.GetColumnValue(&quot;Main.numberroom&quot;);
      else 
        room = &quot;&quot;;
      ((TextObject)sender).Text = (string)Report.GetColumnValue(&quot;Main.str&quot;)+&quot; д.&quot;+(string)Report.GetColumnValue(&quot;Main.home&quot;)+korp+flat+room;
    }

    private void Memo282_BeforePrint(object sender, EventArgs e)
    {
      if ((string)Report.GetColumnValue(&quot;Main.mess&quot;) != &quot;&quot;) 
        ((TextObject)sender).Text = strAudit+(string)Report.GetColumnValue(&quot;Main.mess&quot;);
      else
        ((TextObject)sender).Text = strAudit+(string)Report.GetColumnValue(&quot;Main.company_name&quot;);
      strAudit = &quot;&quot;;
    }

    private void Text5_BeforePrint(object sender, EventArgs e)
    {
      if ((string)Report.GetColumnValue(&quot;Tarif.sname&quot;) != &quot;Дол. финанс. кап. ремонта&quot; )
        ((TextObject)sender).Font = new Font(((TextObject)sender).Font, System.Drawing.FontStyle.Regular);
      else
        ((TextObject)sender).Font = new Font(((TextObject)sender).Font,System.Drawing.FontStyle.Bold);
    }

    private void Text57_BeforePrint(object sender, EventArgs e)
    {
      if ((decimal)Report.GetColumnValue(&quot;Itog.peniout&quot;) &gt; 0) 
        ((TextObject)sender).Visible = true;
      else
        ((TextObject)sender).Visible = false;
    }

    private void Memo41_BeforePrint(object sender, EventArgs e)
    {
      decimal i = (decimal)Report.GetColumnValue(&quot;Itog.out2&quot;);
      decimal sumItog;
      if ((decimal)Report.GetColumnValue(&quot;Itog.peniout&quot;) &lt;= 0)
        sumItog = (decimal)Report.GetColumnValue(&quot;Itog.out2&quot;)+(decimal)Report.GetColumnValue(&quot;Itog.bankproc&quot;);
      else
        if (i &gt; 0) 
        sumItog = (decimal)Report.GetColumnValue(&quot;Itog.out2&quot;)+(decimal)Report.GetColumnValue(&quot;Itog.bankproc&quot;)+(decimal)Report.GetColumnValue(&quot;Itog.peniout&quot;);
      else
        sumItog = (decimal)Report.GetColumnValue(&quot;Itog.peniout&quot;)+(decimal)Report.GetColumnValue(&quot;Itog.bankproc&quot;);
      ((TextObject)sender).Text = string.Format(&quot;{0:### ##0.00 }&quot;,sumItog);
    }

    private void Memo3_BeforePrint(object sender, EventArgs e)
    {
      if ((decimal)Report.GetColumnValue(&quot;Itog.dolg&quot;) != 0) 
      {
        ((TextObject)sender).Visible = true;
        if ((decimal)Report.GetColumnValue(&quot;Itog.dolg&quot;) &gt; 0) 
          ((TextObject)sender).Text = &quot;Долг на &quot;+ string.Format(&quot;{0:d}&quot;,(DateTime)Report.GetColumnValue(&quot;Main.period&quot;));// ((DateTime)Report.GetColumnValue(&quot;Main.period&quot;)).ToShortDateString;
        else
          ((TextObject)sender).Text = &quot;Перепл. на &quot;+ string.Format(&quot;{0:d}&quot;,(DateTime)Report.GetColumnValue(&quot;Main.period&quot;));
      }
      else
        ((TextObject)sender).Visible = false;
    }

