    private void Memo121_BeforePrint(object sender, EventArgs e)
    {
      if ((string)Report.GetColumnValue(&quot;Main.codehelp&quot;) != &quot;&quot; || (string)Report.GetColumnValue(&quot;Main.codesoc&quot;) != &quot;&quot;)
        ((TextObject)sender).Visible = true;
      else
        ((TextObject)sender).Visible = false;
    }

    private void Text86_BeforePrint(object sender, EventArgs e)
    {
      if ((string)Report.GetColumnValue(&quot;Main.codehelp&quot;) != &quot;&quot;)
        ((TextObject)sender).Text = (string)Report.GetColumnValue(&quot;Main.codesoc&quot;)+&quot;(&quot;+(string)Report.GetColumnValue(&quot;Main.codehelp&quot;)+&quot;)&quot;;
      else
        ((TextObject)sender).Text = (string)Report.GetColumnValue(&quot;Main.codesoc&quot;);
    }

    private void Text89_BeforePrint(object sender, EventArgs e)
    {
      if ((decimal)Report.GetColumnValue(&quot;Main.incsoc&quot;) == 0 &amp;&amp; (decimal)Report.GetColumnValue(&quot;Main.calcsoc&quot;) == 0 &amp;&amp; 
        (decimal)Report.GetColumnValue(&quot;Main.topaysoc&quot;) == 0 &amp;&amp; (decimal)Report.GetColumnValue(&quot;Main.outsoc&quot;) == 0) 
        ((TextObject)sender).Visible = false;
      else
        ((TextObject)sender).Visible = true;
    }

    private void Text98_BeforePrint(object sender, EventArgs e)
    {
