    }

    private void Memo24_BeforePrint(object sender, EventArgs e)
    {
      if ((Int16)Report.GetColumnValue(&quot;Counters.service_id&quot;) == 5) 
        ((TextObject)sender).Text = &quot;х.вода&quot;;
      else
        if ((Int16)Report.GetColumnValue(&quot;Counters.service_id&quot;) == 10) 
        ((TextObject)sender).Text = &quot;г.вода&quot;;  
      else
        if ((Int16)Report.GetColumnValue(&quot;Counters.service_id&quot;) == 17 || (Int16)Report.GetColumnValue(&quot;Counters.service_id&quot;) == 23) 
        ((TextObject)sender).Text = &quot;электрич.&quot;;      
      //      else 
      //        ((TextObject)sender).Text = &quot;&quot;; 
    }

    private void Memo37_BeforePrint(object sender, EventArgs e)
    {
      if ((Int16)Report.GetColumnValue(&quot;Counters.service_id&quot;) == 5 || (Int16)Report.GetColumnValue(&quot;Counters.service_id&quot;) == 10) 
        ((TextObject)sender).Text = &quot;м3&quot;;

    }

    private void Data8_BeforePrint(object sender, EventArgs e)
    {
      if ((Int16)(Report.GetColumnValue(&quot;Counters.service_id&quot;)) != 5 &amp;&amp; (Int16)(Report.GetColumnValue(&quot;Counters.service_id&quot;)) != 10 &amp;&amp;
        (Int16)(Report.GetColumnValue(&quot;Counters.service_id&quot;)) != 17 &amp;&amp; (Int16)(Report.GetColumnValue(&quot;Counters.service_id&quot;)) != 23 &amp;&amp;
        (Int16)(Report.GetColumnValue(&quot;Counters.service_id&quot;)) != 1000)
        ((DataBand)sender).Visible = false;
      else
        ((DataBand)sender).Visible = true;
      if (((DataBand)sender).Name == &quot;Data8&quot;)
        cnt +=1;
      else 
        cnt2+=1;
      //Лишние строчки не печатаем
      if  ((Int16)(Report.GetColumnValue(&quot;Counters.service_id&quot;)) == 5 &amp;&amp; ( ( ((DataBand)sender).Name == &quot;Data8&quot; &amp;&amp; cnt&gt;3) || cnt2&gt;3))
      {
        ((DataBand)sender).Visible = false; 
        if (((DataBand)sender).Name == &quot;Data8&quot;)
          cnt --;
        else 
          cnt2--;
      }
      if  ((Int16)(Report.GetColumnValue(&quot;Counters.service_id&quot;)) == 10 &amp;&amp; ( (((DataBand)sender).Name == &quot;Data8&quot; &amp;&amp; cnt&gt;6) || cnt2&gt;6)) 
      {
        ((DataBand)sender).Visible = false; 
        if (((DataBand)sender).Name == &quot;Data8&quot;)
          cnt --;
        else 
          cnt2--;
      }

    }

